using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Pitching : MonoBehaviour {      public GameObject BallPrefab;     public Transform Pitcher;     public float BaseSpeed = 1.0f;      public float InOut;     public float HighLow;     public float BallSpeed = -1.8105f;      /*      // 投球コースのもっとも高いところ     public float UpperLimits = 5.0f;     // 投球コースのもっとも低いところ     public float LowerLimits = -2.0f;      */      // Update is called once per frame     void Update()     {          // 投球スピードを加速する         if (Input.GetKeyDown(KeyCode.K))         {             BallSpeed -= 0.01f;         }         // 投球スピードを減速する         if (Input.GetKeyDown(KeyCode.H))         {             BallSpeed += 0.01f;         }         // 投球を右バッターボックス方向へ         if (Input.GetKeyDown(KeyCode.Y))         {                 InOut -= 0.01f;         }         // 投球を左バッターボックス方向へ         if (Input.GetKeyDown(KeyCode.I))         {                 InOut += 0.01f;         }         // 投球角度をあげる         if (Input.GetKeyDown(KeyCode.U))         {             HighLow += 0.01f;         }         // 投球角度を下げる         if (Input.GetKeyDown(KeyCode.M))         {             HighLow -= 0.01f;         }           if (Input.GetKeyDown(KeyCode.J))         {             // プレハブの野球ボールを複製             GameObject PitchingStart = Instantiate(BallPrefab) as GameObject;             // どこからボールを投げるかを指定する             PitchingStart.transform.position = Pitcher.position;             // 投球の力を加える             PitchingStart.GetComponent<Rigidbody>().AddForce(InOut * BaseSpeed, HighLow * BaseSpeed, BallSpeed * BaseSpeed, ForceMode.Impulse);          }       } } 